	jsFileLocation = jsFileLocation.replace('js/bookmarksearch.js', '');
	$.ajax({
		type: 'POST',
		url: jsFileLocation + 'ajax/recordClick.php',
		data: 'url=' + encodeURI($(this).attr('href')),
	});	
}

